#!/bin/bash

# ======================================================================================================================
# one liner command
# ======================================================================================================================
# sudo apt update -y ; sudo apt install -y wget ; wget https://raw.githubusercontent.com/mugimugi555/userland/main/debian/install_tflite.sh && bash install_tflite.sh ;

# ======================================================================================================================
# install python pip
# ======================================================================================================================
sudo apt update ;
sudo apt upgrade -y ;
sudo apt install -y python3-pip ;

# ======================================================================================================================
# install tf lite
# ======================================================================================================================
pip3 install --extra-index-url https://google-coral.github.io/py-repo/ tflite_runtime ;

# ======================================================================================================================
# finish
# ======================================================================================================================
python3 -c 'import tflite_runtime as tf; print(tf.__version__)' ;
